import { getFirestore, collection, onSnapshot, query } from "firebase/firestore";
export default {
  name: "App",
  components: {},

  data() {
    return {
      producto: {
        color: "",
        columna: ""
      },
      productos: []
    };
  },

  methods: {
    async getProducts() {
      const db = getFirestore();
      const q = query(collection(db, "jirafe"));
      onSnapshot(q, querySnapshot => {
        const productos = [];
        this.productos = [];
        querySnapshot.forEach(doc => {
          productos.push({
            id: doc.id,
            data: doc.data()
          });
        });
        this.productos = productos;
      });
    }

  },

  mounted() {
    this.getProducts();
  }

};
